import React, { useState,  useEffect, useRef} from 'react';
import './App.css';
import KeyModal from './KeyModal';

import "react-chat-elements/dist/main.css"
import { MessageBox, Input } from "react-chat-elements";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUpload } from '@fortawesome/free-solid-svg-icons';
import { Comment } from 'react-loader-spinner';

document.addEventListener('DOMContentLoaded', function() {
  fetch('/delete-files', {
      method: 'POST',
      headers: {
          'Content-Type': 'application/json'
      }
  })
  .then(response => response.json())
  .then(data => console.log('Success:', data))
  .catch((error) => {
      console.error('Error:', error);
  });
});


function App() {
  const [messages, setMessages] = useState([]);
  const [backendOutputs, setBackendOutputs] = useState([]);
  const [verbose, setVerbose] = useState([]);
  // const [description, setDescription] = useState(''); 
  const [descriptions, setDescriptions] = useState({});

  const [extractedSentences, setExtractedSentences] = useState([]);
  const [query, setQuery] = useState('');

  const [imageUrl, setImageUrl] = useState('');
  const [showImage, setShowImage] = useState(false);

  const [file, setFile] = useState(null);

  const [isModalOpen, setIsModalOpen] = useState(true);

  const [isLoading, setIsLoading] = useState(false);
  const [displayRHS, setDisplayRHS] = useState(true);
  const [warning, setWarning] = useState(false);
  const [leapwarning, setLeapwarning] = useState(false);

  const [filePaths, setFilePaths] = useState({result: '', augmentedTable: ''});
  const [timestamp, setTimestamp] = useState('');

  const [dataheader, setDataheader] = useState([]);

  const [code, setCode] = useState([]);

  const [isToggled, setIsToggled] = useState(false);

  const fixedDivRef = useRef(null);
  const [fixedDivHeight, setFixedDivHeight] = useState(0);

  useEffect(() => {
    if (fixedDivRef.current) {
      // Recalculate the height when dependencies change
      setFixedDivHeight(fixedDivRef.current.clientHeight);
    }
  }, [displayRHS, file, dataheader]);

  const toggle = () => {
    setIsToggled(!isToggled);
  };

  useEffect(() => {
      // Automatically open the modal when the page loads
      setIsModalOpen(true);
  }, []);

  // require the highlight.js library, including all languages
  const hljs = require('./highlight.js');
  const highlightedCode = hljs.highlightAuto('<span>Hello World!</span>').value

  const handleKeySubmit = async (apikey, org) => {
    setIsModalOpen(false); // Close the modal after submitting
    // Here you can call your backend API
    try {
        const response = await fetch('http://127.0.0.1:5000/process_key', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ apikey, org }),
        });
        const data = await response.json();
        setIsModalOpen(false); // Close the modal after submitting

    } catch (error) {
        console.error('Error submitting password:', error);
    }
};

  const handleCopyToClipboard = (sentence) => {
    navigator.clipboard.writeText(sentence)
      .then(() => {
        console.log('Sentence copied to clipboard:', sentence);
      })
      .catch((error) => {
        console.error('Error copying sentence to clipboard:', error);
      });
  };


  const handleDescriptionChange = (header, value) => {
    setDescriptions(prev => ({
        ...prev,
        [header]: value
    }));
  };

  const handleFileChange = (event) => {
    const f = event.target.files[0];
    setFile(f);  // Update the state if needed elsewhere in your component
    handleUpload(f);  // Pass file directly
  };

  const handleUpload = (f) => {
    // alert('Please select a file first!');
    if (!f) {
      return;
    }

    const formData = new FormData();
    formData.append('file', f);

    fetch('http://127.0.0.1:5000/upload-csv', {
      method: 'POST',
      body: formData,
    })
    .then(response => {
      if (response.ok) {
        return response.json();
      }
      throw new Error('Network response was not ok.');
    })
    .then(data => {
      setDataheader(data.headers);  // Update the state with the headers
      setMessages(prev => [...prev, { text: "We receive your data " + f.name + "!", type: "text", from: 'bot' }]); 
      setMessages(prev => [...prev, { text: "Remember to submit the data descriptions together with your queries! Specifically, briefly describe the contents of each column of your provided data. For example, \n Post: Tweets to be analyzed \n Date: Tweet dates", type: "text", from: 'bot' }]);
    })
    .catch(error => {
      console.error('Error:', error);
    });

    const initialDescriptions = {};
        dataheader.forEach(header => {
            initialDescriptions[header] = '';  // Initialize each header's description as an empty string
        });
    setDescriptions(initialDescriptions);
  };

  useEffect(() => {
    // Polling mechanism to periodically check for the image
    const interval = setInterval(() => {
      // Update the image URL with a new timestamp to avoid cache issues
      setImageUrl(`/static/dot_graph.png?timestamp=${new Date().getTime()}`);
      setShowImage(true); 
    }, 5000); // Update every 5 seconds

    return () => clearInterval(interval); // Cleanup on component unmount
  }, []);

  const handleHover = (event) => {
    event.target.style.color = 'white'; // Change text color on hover
    event.target.style.borderColor = 'white'; // Change border color on hover
  };

  const handleHoverOut = (event) => {
    event.target.style.color = 'grey'; // Reset text color when hover ends
    event.target.style.borderColor = 'grey'; // Reset border color when hover ends
  };

  const handleSendMessage = (message) => {
    const payload = {
      message,  // the message text from the input
      descriptions  // the current state of the description input
    };
    // POST request to start the count
    fetch('http://127.0.0.1:5000/start_count', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload)
    })

    .then(() => {
      // Establish an EventSource connection to receive the count
      const eventSource = new EventSource('http://127.0.0.1:5000/test_stream');
      eventSource.onmessage = event => {
        const data = event.data.trim();
        if (data === "start") {
          setIsLoading(true);
        }
        else if (data === "complete") {
          setIsLoading(false);
        }
        else if (data.startsWith("VER NUMBER:")) {
          const ver=data.slice("VER NUMBER:".length).trim();
          setTimestamp(ver);
        }
        else if (data.startsWith("CODE:")) {
          const c=data.slice("CODE:".length).trim();
          setCode(prevCode => [...prevCode, c]);
        }
        else if (data.startsWith("CACHE:")) {
          const filename=data.slice("CACHE:".length).trim();
          const regex = /result(\d+)\.csv$/;
          const match = filename.match(regex);
          if (match && match[1]) {
            setTimestamp(match[1]);
          }
          setFilePaths({result: "./uploads/result.csv"});
          setMessages(prev => [...prev, { text: "Download Full Result", type: "file", version: timestamp, from: 'bot' }]);
        }
        else if (data.startsWith("########## Warning: ")) {
          const warning_msg=data.slice("########## Warning: ".length).trim();
          setMessages(prev => [...prev, { text: warning_msg, type: "text", from: 'bot' }]);
          setWarning(true);
        }
        else if (data.startsWith("########## LEAPWARNING: ")) {
          const warning_msg=data.slice("########## LEAPWARNING: ".length).trim();
          setMessages(prev => [...prev, { text: warning_msg, type: "text", from: 'bot' }]);
          setLeapwarning(true);
        }
        else{          
          if (data.startsWith("########## Feedback: ")) {
            const remainingMessage = data.slice("########## Feedback: ".length).trim(); 
            const pattern = /\*\*(.*?)\*\*/g;
            const matches = remainingMessage.match(pattern);
            if (matches) {
              const extractedSentences = matches.map(match => match.slice(2, -2));
              const replacedMessage = remainingMessage.replace(pattern, match => match.replace(/\*\*/g, "'"));
              setMessages(prev => [...prev, { text: replacedMessage, type: "text", from: 'bot' }]);
              setExtractedSentences(extractedSentences);
            } else {
              setMessages(prev => [...prev, { text: remainingMessage, type: "text", from: 'bot' }]);
            }
          } 
          else if (data.startsWith("########## AT-HTML:")){
            const html = data.slice("########## AT-HTML:".length).trim();
            setMessages(prev => [...prev, { text: "Data labelled:)", display_html: true, htmlContent: html, type: "text", from: 'bot' }]);

            setFilePaths({augmentedTable: "./uploads/augmented_table.csv"});
            setMessages(prev => [...prev, { text: "Download Full Labelled Table", type: "file", version: timestamp, from: 'bot' }]);
          }
          else if (data.startsWith("########## R-HTML:")){
            const html = data.slice("########## R-HTML:".length).trim();
            setMessages(prev => [...prev, { text: "Result generated:)", display_html: true, htmlContent: html, type: "text", from: 'bot' }]);

            setFilePaths({result: "./uploads/result.csv"});
            setMessages(prev => [...prev, { text: "Download Full Result", type: "file", version: timestamp, from: 'bot' }]);
          }
          else if (data.startsWith("VERBOSE:")){
            const data_new = data.slice("VERBOSE:".length).trim();
            setVerbose(prev => [...prev, false]);
            setBackendOutputs(prev => [...prev, data_new]);
          }
          else {
            setBackendOutputs(prev => [...prev, data]);
            setVerbose(prev => [...prev, true]);
          }
        }
      };
      eventSource.onerror = error => {
        console.error('EventSource error:', error);
        eventSource.close();
      };
    })

    .catch(error => console.error('Error:', error));

    setMessages(prev => [...prev, { text: message, type: "text", from: 'user' }]);
    // setDescription('');
  };

  function handleDownload(text,timestamp) {
    const uri = text === "Download Full Result" ? './uploads/result'+String(timestamp)+'.csv' : './uploads/augmented_table'+String(timestamp)+'.csv';
    fetch(uri)
      .then(response => response.blob())
      .then(blob => {
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = uri.split('/').pop(); // Assuming the filename is the last part of the URI
        document.body.appendChild(a);
        a.click();
        a.remove();
      })
      .catch(e => console.error('Download failed:', e));
  }

  const handleDeleteFiles = async () => {
    try {
        const response = await fetch('/delete-dot-graph', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
        });

        if (!response.ok) {
            throw new Error('Failed to delete files');
        }
        const result = await response.json();
        console.log(result.message); // Display the server response message
    } catch (error) {
        console.error('Error deleting files:', error);
    }
};

const handleWarning = (shouldWarn) => {
  // Create the data object to be sent to the backend
  const data = {
    warn: shouldWarn
  };

  // POST request to start the count
  fetch('http://127.0.0.1:5000/warning', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(data)
  })

  setWarning(false);
};

const handleLeapWarning = (shouldWarn) => {
  // Create the data object to be sent to the backend
  const data = {
    warn: shouldWarn
  };

  // POST request to start the count
  fetch('http://127.0.0.1:5000/leap-warning', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(data)
  })

  setLeapwarning(false);
};

  const handleMessageInput = event => {
    if (event.key === 'Enter' && event.target.value.trim() !== '') {
      setCode([]);
      handleDeleteFiles();
      // handleUpload();
      handleSendMessage(event.target.value);
      event.target.value = '';
      setBackendOutputs([]);
      setVerbose([]);
      // setFilePaths({result: "", augmentedTable: ""});
      setExtractedSentences([]);
      setQuery('');
    }
  };

  return (
    <div className="App">
      <KeyModal isOpen={isModalOpen} onSubmit={handleKeySubmit} /> 
      <div className="grid-container" style={{ gridTemplateColumns: displayRHS ? "1fr 1fr" : "1fr" }}>
        <div className="chat-section">
          {/* <header className="App-header"> */}
          <div style = {{backgroundColor: "#282c34", height: `calc(100vh - ${fixedDivHeight}px)`, overflowY: 'auto', width: '100%'}}>
          <ul>
            {messages.map((msg, index) => (
              <div key={index} style={{ textAlign: msg.from === 'user' ? 'right' : 'left' }}>
                {msg.from === 'user' ? (
                  <MessageBox
                    position='right'
                    type='text'
                    text={msg.text}
                    // date={new Date()}
                  />
                ) : (
                  <>
                    <MessageBox
                      position='left'
                      type={msg.type}
                      title="LEAP"
                      text={msg.text}
                      // date={new Date()}
                      onTitleClick={() => setDisplayRHS(!displayRHS)}
                      data={{
                        uri: filePaths.augmentedTable,
                        status: {
                          click: false,
                          loading: 0,
                        },
                      }}
                      onDownload={msg.type === 'file' ? () => handleDownload(msg.text, timestamp) : undefined}
                    />
                    {msg.display_html && (
                      <div style={{  width: '90%', fontSize: '10px', display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '20px' , marginTop: '10px', marginBottom: '10px'}}                 onMouseEnter={handleHover} // Change color on hover
                      onMouseLeave={handleHoverOut} dangerouslySetInnerHTML={{ __html: msg.htmlContent }} />
                    )}
                  </>
                )}
              </div>
            ))}
          </ul>
          <div>
            {warning && (
              <div style={{ justifyContent: 'center'}}>
                <div style={{ marginTop: '-15px', width:'50%', display: 'flex', justifyContent: 'center', gap: '10px' }}>
                  <div style={{ borderColor: 'grey', color: 'grey', width: '70%', fontSize: '15px', marginLeft: 'auto', marginRight: 'auto', marginBottom: '10px', border: '1px solid #ccc', padding: '5px' }} onClick={() => handleWarning(true)} onMouseEnter={handleHover} onMouseLeave={handleHoverOut}>YES</div>
                  <div style={{ borderColor: 'grey', color: 'grey', width: '70%', fontSize: '15px', marginLeft: 'auto', marginRight: 'auto', marginBottom: '10px', border: '1px solid #ccc', padding: '5px' }} onClick={() => handleWarning(false)} onMouseEnter={handleHover} onMouseLeave={handleHoverOut}>NO</div>
                </div>
              </div>
            )}
          </div> 

          <div>
            {leapwarning && (
              <div style={{ justifyContent: 'center'}}>
                <div style={{ marginTop: '-15px', width:'50%', display: 'flex', justifyContent: 'center', gap: '10px' }}>
                  <div style={{ borderColor: 'grey', color: 'grey', width: '70%', fontSize: '15px', marginLeft: 'auto', marginRight: 'auto', marginBottom: '10px', border: '1px solid #ccc', padding: '5px' }} onClick={() => handleLeapWarning(false)} onMouseEnter={handleHover} onMouseLeave={handleHoverOut}>YES</div>
                  <div style={{ borderColor: 'grey', color: 'grey', width: '70%', fontSize: '15px', marginLeft: 'auto', marginRight: 'auto', marginBottom: '10px', border: '1px solid #ccc', padding: '5px' }} onClick={() => handleLeapWarning(true)} onMouseEnter={handleHover} onMouseLeave={handleHoverOut}>NO</div>
                </div>
              </div>
            )}
          </div> 

            <div className="extracted-section">
              {extractedSentences && extractedSentences.map((sentence, index) => (
                <div key={index} 
                style={{ width: '70%', fontSize: '15px', marginLeft: 'auto', marginRight: 'auto', marginBottom: '10px', border: '1px solid #ccc', padding: '5px' }}
                onClick={() => {
                  handleCopyToClipboard(sentence);
                  setQuery(sentence);
                  setExtractedSentences([]);
                }}
                onMouseEnter={handleHover} // Change color on hover
                onMouseLeave={handleHoverOut} // Reset color when hover ends
                >
                  {sentence}
                </div>
              ))}
            </div>
            </div>

            <div ref={fixedDivRef} style={{
                position: 'fixed', // This makes the element fixed relative to the viewport
                bottom: '0',        // Positions the element at the bottom of the viewport
                left: '0',          // Aligns the element to the left of the viewport
                right: '0',         // Aligns the element to the right of the viewport
                width: displayRHS ? '50%' : '100%',      // Ensures the element spans the full width of the viewport
                fontSize: '15px',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                alignItems: 'center',
                backgroundColor: '#282c34', // Optional: Change the background color if needed
                zIndex: '1000',
                borderTop: '1px solid #FFFFFF', // Adds a white top border with 3px thickness

              }}>
            <div style={{ color: 'white', marginTop: '10px', fontSize: '15px', display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '20px' , marginBottom: '10px'}}>
            <div>
              <label className="file-input-label">
                <input
                  type="file"
                  onChange={handleFileChange}
                  accept=".csv"
                  style={{ display: 'none' }}
                />
                <span className="file-input-button">
                  <FontAwesomeIcon icon={faUpload} /> Upload Data
                </span>
              </label>
            </div>

            <div>
            {dataheader.length > 0 && (
                <>
                    {/* <h3>Data Descriptions:</h3>
                    Please briefly describe the contents of each column of your provided data. For example, 
                    <br></br>
                    Post: <b>Tweets to be analyzed</b>
                    <br></br>
                    Date: <b>Tweet dates</b> */}
                    <ul>
                        {dataheader.map((header, index) => (
                            <div key={index} className="list-item">
                            <span className="header">{header}:</span>
                            <div className="input-container">
                                  <Input
                                    type="text"
                                    value={descriptions[header] || ''}
                                    onChange={(e) => handleDescriptionChange(header, e.target.value)}
                                    placeholder="Type column description..."
                                  />
                                </div>
                            </div>
                        ))}
                    </ul>
                </>
            )}
        </div>
          </div>

          {file && (
                <div style={{ fontSize: '15px', marginBottom: '10px', color: 'white'}}>
                  Uploaded File: {file.name}
                </div>
              )}
            <div style={{ width: '50%', maxWidth: '800px', margin: '0 auto', marginBottom: '10px'}}>
             { isLoading ? (
           <Comment
           visible={true}
           height="80"
           width="80"
           ariaLabel="comment-loading"
           wrapperStyle={{}}
           wrapperClass="comment-wrapper"
           color="#fff"
           backgroundColor="grey"
           />
             ) : (<Input type="text" value={query} onKeyDown={handleMessageInput} onChange={(e) => setQuery(e.target.value)} placeholder="Message LEAP..." />)
             }
            </div>
          </div>

          {/* </header> */}
        </div>


        {displayRHS&&( 
        <div className="output-section" style={{ height: '100vh'}}>
                  {
        <div style={{
          position: 'fixed',
          bottom: 0,
          width: '50%',
          // backgroundColor: '#fff',
          // boxShadow: '0px -4px 10px rgba(0,0,0,0.1)',
          textAlign: 'center',
          padding: '20px',
          height: '15vh',
          overflowY: 'auto'
        }}>
          <h2>LEAP's Codespace</h2>
          <pre><code>
          {code.map((line, index) => (
            <React.Fragment key={index}>
              {line}
              {'\n'} 
            </React.Fragment>
          ))}
        </code></pre>

        </div>
      }
          <div style={{ height: code ? '80vh' : '100vh', textAlign: "center", overflowY: 'auto', flexGrow: 1}}>
          <h2>LEAP's Workspace
          </h2>
          <div className="toggle-container">
              <div className="toggle-switch">
                <input
                  type="checkbox"
                  id="toggle"
                  className="toggle-checkbox"
                  checked={isToggled}
                  onChange={toggle}
                />
                <label className="toggle-label" htmlFor="toggle">
                  <span className="toggle-inner" />
                  <span className="toggle-switch" />
                </label>
              </div>
              <label htmlFor="toggle" className="toggle-text">Verbose Mode</label>
            </div>
          <div>
          {showImage && (
            <div>
              <img src={imageUrl} alt="Dynamic from backend" onError={(e) => {
                console.error('Failed to load image:', imageUrl);
                setShowImage(false); // Hide the image if there is an error loading it
              }} />
              <div className="subtitle">Column Mapping Relationship</div>
            </div>
          )}
          </div>
          <pre>
          {backendOutputs.map((output, index) => {
          // Check if output should be displayed based on isToggled and the verbose array
          if (isToggled || verbose[index]) {
            return <div key={index}>{output}</div>;
          }
          return null; // Do not render this output if conditions aren't met
        })}
          </pre>
        </div>

        </div>
        )
      }
      </div>
    </div>
  );
}

export default App;
