import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [backendOutputs, setBackendOutputs] = useState([]);
  const [eventSource, setEventSource] = useState(null);

  useEffect(() => {
    // Clean up the event source when the component unmounts or when a new source is created
    return () => {
      if (eventSource) {
        eventSource.close();
      }
    };
  }, [eventSource]);

  const handleSendMessage = (message) => {
    if (!eventSource) { // Check if an event source already exists, if not, create it
      const newEventSource = new EventSource('http://127.0.0.1:5000/start_count', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message })
      });

      newEventSource.onmessage = event => {
        console.log('Received event:', event.data);
        setBackendOutputs(prev => [...prev, event.data.trim()]);
      };

      newEventSource.onerror = error => {
        console.error('EventSource error:', error);
        newEventSource.close();
        setEventSource(null);
      };

      setEventSource(newEventSource);
    }

    // Add the message to local messages state
    setMessages(prev => [...prev, { text: message, from: 'user' }]);
  };

  const handleMessageInput = event => {
    if (event.key === 'Enter' && event.target.value.trim() !== '') {
      handleSendMessage(event.target.value);
      event.target.value = '';  // Clear input after sending
    }
  };

  return (
    <div className="App">
      <div className="grid-container">
        <div className="chat-section">
          <header className="App-header">
            <ul>
              {messages.map((msg, index) => (
                <li key={index} style={{ textAlign: msg.from === 'user' ? 'right' : 'left' }}>
                  {msg.text}
                </li>
              ))}
            </ul>
            <input type="text" onKeyDown={handleMessageInput} placeholder="Type a message..." />
          </header>
        </div>
        <div className="output-section">
          <h2>Backend Execution Output:</h2>
          <pre>
            {backendOutputs.map((output, index) => (
              <div key={index}>{output}</div>
            ))}
          </pre>
        </div>
      </div>
    </div>
  );
}

export default App;


