import React, { useState } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [backendOutputs, setBackendOutputs] = useState([]);

  const handleSendMessage = (message) => {
    fetch('/display_chat', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ input: message })
    })
    .then(response => response.json())
    .then(data => {
      const output = data.output;
      if (output.startsWith("Received")) {
        setMessages(prev => [...prev, { text: output, from: 'bot' }]);
      } else {
        setBackendOutputs(prev => [...prev, output]);
      }
    })
    .catch(error => console.error('Error:', error));
  };

  const handleMessageInput = event => {
    if (event.key === 'Enter' && event.target.value.trim() !== '') {
      const newMessage = event.target.value;
      setMessages(prev => [...prev, { text: newMessage, from: 'user' }]);
      handleSendMessage(newMessage);
      event.target.value = '';  // Clear input after sending
    }
  };

  return (
    <div className="App">
      <div className="grid-container">
        <div className="chat-section">
          <header className="App-header">
            <ul>
              {messages.map((msg, index) => (
                <li key={index} style={{ textAlign: msg.from === 'user' ? 'right' : 'left' }}>
                  {msg.text}
                </li>
              ))}
            </ul>
            <input type="text" onKeyDown={handleMessageInput} placeholder="Type a message..." />
          </header>
        </div>
        <div className="output-section">
          <h2>Backend Execution Output:</h2>
          <pre>
            {backendOutputs.map((output, index) => (
              <div key={index}>{output}</div>
            ))}
          </pre>
        </div>
      </div>
    </div>
  );
}

export default App;
