import React, { useState } from 'react';
import './App.css'; 

function App() {
  const [messages, setMessages] = useState([]);
  const [backendOutput, setBackendOutput] = useState('');

  const handleSendMessage = (message) => {
    fetch('/display_chat', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ input: message })
    })
    .then(response => response.json())
    .then(data => {
      setMessages(prev => [...prev, { text: data.output, from: 'bot' }]);
    })
    .catch(error => console.error('Error:', error));
  };

  const handleMessageInput = event => {
    if (event.key === 'Enter' && event.target.value.trim() !== '') {
      const newMessage = event.target.value;
      setMessages(prev => [...prev, { text: newMessage, from: 'user' }]);
      handleSendMessage(newMessage);
      handleFetchBackendOutput();
      event.target.value = '';  // Clear input after sending
    }
  };

  const handleFetchBackendOutput = () => {
    fetch('/run-backend')
      .then(response => response.json())
      .then(data => {
        if (data.output) {
          setBackendOutput(data.output);  // Directly set the state without an intermediary function
        }
      })
      .catch(error => console.error('Error:', error));
  };


  return (
    <div className="App">
      <div className="grid-container">
        <div className="chat-section">
          <header className="App-header">
            <ul>
              {messages.map((msg, index) => (
                <li key={index} style={{ textAlign: msg.from === 'user' ? 'right' : 'left' }}>
                  {msg.text}
                </li>
              ))}
            </ul>
            <input type="text" onKeyDown={handleMessageInput} placeholder="Type a message..." />
          </header>
        </div>
        <div className="output-section">
          <h2>Backend Execution Output:</h2>
          <pre>{backendOutput}</pre>
        </div>
      </div>
    </div>
  );


}

export default App;
