import React, { useState,  useEffect } from 'react';
import './App.css';
import KeyModal from './KeyModal';

import "react-chat-elements/dist/main.css"
import { MessageBox, Input } from "react-chat-elements";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUpload } from '@fortawesome/free-solid-svg-icons';
import { Comment } from 'react-loader-spinner';

document.addEventListener('DOMContentLoaded', function() {
  fetch('/delete-files', {
      method: 'POST',
      headers: {
          'Content-Type': 'application/json'
      }
  })
  .then(response => response.json())
  .then(data => console.log('Success:', data))
  .catch((error) => {
      console.error('Error:', error);
  });
});


function App() {
  const [messages, setMessages] = useState([]);
  const [backendOutputs, setBackendOutputs] = useState([]);
  // const [description, setDescription] = useState(''); 
  const [descriptions, setDescriptions] = useState({});

  const [extractedSentences, setExtractedSentences] = useState([]);
  const [query, setQuery] = useState('');

  const [imageUrl, setImageUrl] = useState('');
  const [showImage, setShowImage] = useState(false);

  const [file, setFile] = useState(null);

  const [isModalOpen, setIsModalOpen] = useState(true);

  const [isLoading, setIsLoading] = useState(false);

  const [filePaths, setFilePaths] = useState({result: '', augmentedTable: ''});
  const [timestamp, setTimestamp] = useState('');

  const [dataheader, setDataheader] = useState([]);

  useEffect(() => {
      // Automatically open the modal when the page loads
      setIsModalOpen(true);
  }, []);

  const handleKeySubmit = async (apikey, org) => {
    setIsModalOpen(false); // Close the modal after submitting
    // Here you can call your backend API
    try {
        const response = await fetch('http://127.0.0.1:5000/process_key', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ apikey, org }),
        });
        const data = await response.json();
        setIsModalOpen(false); // Close the modal after submitting

    } catch (error) {
        console.error('Error submitting password:', error);
    }
};

  const handleCopyToClipboard = (sentence) => {
    navigator.clipboard.writeText(sentence)
      .then(() => {
        console.log('Sentence copied to clipboard:', sentence);
      })
      .catch((error) => {
        console.error('Error copying sentence to clipboard:', error);
      });
  };


  const handleDescriptionChange = (header, value) => {
    setDescriptions(prev => ({
        ...prev,
        [header]: value
    }));
  };

  const handleFileChange = (event) => {
    const f = event.target.files[0];
    setFile(f);  // Update the state if needed elsewhere in your component
    handleUpload(f);  // Pass file directly
  };

  const handleUpload = (f) => {
    // alert('Please select a file first!');
    if (!f) {
      return;
    }

    const formData = new FormData();
    formData.append('file', f);

    fetch('http://127.0.0.1:5000/upload-csv', {
      method: 'POST',
      body: formData,
    })
    .then(response => {
      if (response.ok) {
        return response.json();
      }
      throw new Error('Network response was not ok.');
    })
    .then(data => {
      setDataheader(data.headers);  // Update the state with the headers
    })
    .catch(error => {
      console.error('Error:', error);
    });

    const initialDescriptions = {};
        dataheader.forEach(header => {
            initialDescriptions[header] = '';  // Initialize each header's description as an empty string
        });
    setDescriptions(initialDescriptions);
  };

  useEffect(() => {
    // Polling mechanism to periodically check for the image
    const interval = setInterval(() => {
      // Update the image URL with a new timestamp to avoid cache issues
      setImageUrl(`/static/dot_graph.png?timestamp=${new Date().getTime()}`);
      setShowImage(true); 
    }, 5000); // Update every 5 seconds

    return () => clearInterval(interval); // Cleanup on component unmount
  }, []);

  const handleHover = (event) => {
    event.target.style.color = 'white'; // Change text color on hover
    event.target.style.borderColor = 'white'; // Change border color on hover
  };

  const handleHoverOut = (event) => {
    event.target.style.color = ''; // Reset text color when hover ends
    event.target.style.borderColor = ''; // Reset border color when hover ends
  };

  const handleSendMessage = (message) => {
    const payload = {
      message,  // the message text from the input
      descriptions  // the current state of the description input
    };
    // POST request to start the count
    fetch('http://127.0.0.1:5000/start_count', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload)
    })

    .then(() => {
      // Establish an EventSource connection to receive the count
      const eventSource = new EventSource('http://127.0.0.1:5000/test_stream');
      eventSource.onmessage = event => {
        const data = event.data.trim();
        if (data === "start") {
          setIsLoading(true);
        }
        else if (data === "complete") {
          setIsLoading(false);
        }
        else if (data.startsWith("VER NUMBER:")) {
          const ver=data.slice("VER NUMBER:".length).trim();
          setTimestamp(ver);
          setIsLoading(false);
        }
        else{          
          if (data.startsWith("########## Feedback: ")) {
            const remainingMessage = data.slice("########## Feedback: ".length).trim(); // Remove "WARNING:" and trim any extra whitespace
            const pattern = /\*\*(.*?)\*\*/g;
            const matches = remainingMessage.match(pattern);
            if (matches) {
              const extractedSentences = matches.map(match => match.slice(2, -2));
              const replacedMessage = remainingMessage.replace(pattern, match => match.replace(/\*\*/g, "'"));
              setMessages(prev => [...prev, { text: replacedMessage, type: "text", from: 'bot' }]);
              setExtractedSentences(extractedSentences);
            } else {
              setMessages(prev => [...prev, { text: remainingMessage, type: "text", from: 'bot' }]);
            }
          } else {
            if (data === "Succeed!") {
              setFilePaths({result: "./uploads/result.csv", augmentedTable: "./uploads/augmented_table.csv"});
              setMessages(prev => [...prev, { text: "Result", type: "file", version: timestamp, from: 'bot' }]);
              setMessages(prev => [...prev, { text: "Augmented Table", type: "file", version: timestamp, from: 'bot' }]);
            }
            setBackendOutputs(prev => [...prev, data]);
          }
        }
      };
      eventSource.onerror = error => {
        console.error('EventSource error:', error);
        eventSource.close();
      };
    })

    .catch(error => console.error('Error:', error));

    setMessages(prev => [...prev, { text: message, type: "text", from: 'user' }]);
    // setDescription('');
  };

  function handleDownload(text,timestamp) {
    const uri = text === "Result" ? './uploads/result'+String(timestamp)+'.csv' : './uploads/augmented_table'+String(timestamp)+'.csv';
    fetch(uri)
      .then(response => response.blob())
      .then(blob => {
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = uri.split('/').pop(); // Assuming the filename is the last part of the URI
        document.body.appendChild(a);
        a.click();
        a.remove();
      })
      .catch(e => console.error('Download failed:', e));
  }

  const handleDeleteFiles = async () => {
    try {
        const response = await fetch('/delete-dot-graph', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
        });

        if (!response.ok) {
            throw new Error('Failed to delete files');
        }
        const result = await response.json();
        console.log(result.message); // Display the server response message
    } catch (error) {
        console.error('Error deleting files:', error);
    }
};

  const handleMessageInput = event => {
    if (event.key === 'Enter' && event.target.value.trim() !== '') {
      handleDeleteFiles();
      // handleUpload();
      handleSendMessage(event.target.value);
      event.target.value = '';
      setBackendOutputs([]);
      // setFilePaths({result: "", augmentedTable: ""});
      setQuery('');
    }
  };

  return (
    <div className="App">
      <KeyModal isOpen={isModalOpen} onSubmit={handleKeySubmit} /> 
      <div className="grid-container">
        <div className="chat-section">
          <header className="App-header">
            {/* <ul>
              {messages.map((msg, index) => (
                <li key={index} style={{ textAlign: msg.from === 'user' ? 'right' : 'left' }}>
                  {msg.text}
                </li>
              ))}
            </ul> */}
                  <ul>
        {messages.map((msg, index) => (
          <div key={index} style={{ textAlign: msg.from === 'user' ? 'right' : 'left' } }>
          {msg.from === 'user' ? (
            <MessageBox
              position='right'
              type='text'
              text={msg.text}
              // date={new Date()}
            />
          ) : (
            <MessageBox
              position='left'
              // type='text'
              type={msg.type}
              title="LEAP"
              text={msg.text}
              // date={new Date()}
              onTitleClick={setDisplayRHS(true)}
              data={{
                uri: filePaths.augmentedTable,
                status: {
                  click: false,
                  loading: 0,
                },
              }}
              onClick={msg.type === 'file' ? () => handleDownload(msg.text, timestamp) : () => {}}
            />

          )}
          </div>
        ))}
      </ul>
            <div className="extracted-section">
              {extractedSentences && extractedSentences.map((sentence, index) => (
                <div key={index} 
                style={{ width: '70%', fontSize: '15px', marginLeft: 'auto', marginRight: 'auto', marginBottom: '10px', border: '1px solid #ccc', padding: '5px' }}
                onClick={() => {
                  handleCopyToClipboard(sentence);
                  setQuery(sentence);
                  setExtractedSentences([]);
                }}
                onMouseEnter={handleHover} // Change color on hover
                onMouseLeave={handleHoverOut} // Reset color when hover ends
                >
                  {sentence}
                </div>
              ))}
            </div>
            <div style={{  width: '50%', fontSize: '15px', display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '20px' , marginBottom: '10px'}}>
            <div>
              <label className="file-input-label">
                <input
                  type="file"
                  onChange={handleFileChange}
                  accept=".csv"
                  style={{ display: 'none' }}
                />
                <span className="file-input-button">
                  <FontAwesomeIcon icon={faUpload} /> Upload Data
                </span>
              </label>
            </div>
            <div>
            {dataheader.length > 0 && (
                <>
                    <h3>Type Data Descriptions:</h3>
                    <ul>
                        {dataheader.map((header, index) => (
                            <div key={index} className="list-item">
                            <span className="header">{header}</span>
                            <div className="input-container">
                                  <Input
                                    type="text"
                                    value={descriptions[header] || ''}
                                    onChange={(e) => handleDescriptionChange(header, e.target.value)}
                                    // placeholder="Type column description..."
                                  />
                                </div>
                            </div>
                        ))}
                    </ul>
                </>
            )}
        </div>
            {/* <h3>Data descriptions:</h3>
      <ul>
        {dataheader.map((header, index) => (
          <li key={index}>{header}</li>
        //   <Input
        //   type="text"
        //   value={description}
        //   onChange={handleDescriptionChange}
        //   placeholder="Type data description..."
        // />
        ))}
      </ul>
            <div style={{ width: '50%', maxWidth: '800px' }}>
              <Input
                type="text"
                value={description}
                onChange={handleDescriptionChange}
                placeholder="Type data description..."
              />
            </div> */}
          </div>
          {file && (
                <div style={{ fontSize: '15px', marginBottom: '10px'}}>
                  Uploaded File: {file.name}
                </div>
              )}
            <div style={{ width: '50%', maxWidth: '800px', margin: '0 auto' }}>
             { isLoading ? (
           <Comment
           visible={true}
           height="80"
           width="80"
           ariaLabel="comment-loading"
           wrapperStyle={{}}
           wrapperClass="comment-wrapper"
           color="#fff"
           backgroundColor="grey"
           />
             ) : (<Input type="text" value={query} onKeyDown={handleMessageInput} onChange={(e) => setQuery(e.target.value)} placeholder="Type query..." />)
             }
            </div>
          </header>
        </div>
        <div className="output-section">
          <h2>LEAP Output:</h2>
          <div>
          {showImage && (
            <div>
              <img src={imageUrl} alt="Dynamic from backend" onError={(e) => {
                console.error('Failed to load image:', imageUrl);
                setShowImage(false); // Hide the image if there is an error loading it
              }} />
              <div className="subtitle">Column Mapping Relationship</div>
            </div>
          )}
          </div>
          <pre>
            {backendOutputs.map((output, index) => (
              <div key={index}>{output}</div>
            ))}
          </pre>
        </div>
      </div>
    </div>
  );
}

export default App;
