type Props = {
    type: "incoming" | "outgoing",
    last?: boolean,
    single?: boolean,

}
export const getBorderCss = ({
    type,
    last,
    single
}: Props) => {

    let borderTopLeft, borderTopRight, borderBottomLeft, borderBottomRight

    if (type === "outgoing") {
        borderTopLeft = true
        borderBottomLeft = true
        borderBottomRight = last ? true : false
        borderTopRight = !last && single ? true : false
    } else {
        borderTopRight = true
        borderBottomRight = true
        borderBottomLeft = single || last ? true : false
        borderTopLeft = last ? true : false
    }

    return `
    border-top-left-radius: ${borderTopLeft ? "8px" : "2px"};
    border-top-right-radius: ${borderTopRight ? "8px" : "2px"};
    border-bottom-left-radius: ${borderBottomLeft ? "8px" : "2px"};
    border-bottom-right-radius: ${borderBottomRight ? "8px" : "2px"};
    `

}

import React from 'react'
import styled from 'styled-components'
import { MediaType } from '../../../types/MessageType'
import { getBorderCss } from '../borderController'


interface Props extends MediaType {
    last?: boolean
    single?: boolean
    messageType: "incoming" | "outgoing"
}

const ImageContainer = styled.div`
    width: 99%;
    padding: 1px;
    position: relative;
user-select: none;

    `

const Image = styled.img<{
    borderCss: string,
}>`
    width: 100%;
    margin: 0px;
    position: relative;

    ${({ borderCss }) => borderCss};

 `

const FileContainer = styled.a`
text-align:left;
vertical-align:text-top;
font-size:14px;
align-self:flex-start;
line-height:auto;
color:#000000;
font-family: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
padding-left:16px;
padding-right:16px;
padding-top:8px;
padding-bottom:8px;
position: relative;
box-sizing: border-box;
word-wrap: break-word;
width: 100%;
text-decoration: none;
user-select: none;
`

const SizeText = styled.span`
margin-left: 6px;
font-size: 11px;

`

const DownloadIcon = <svg
    xmlns="http://www.w3.org/2000/svg"
    width="16"
    height="16"
    fill="none"
    viewBox="0 0 24 24"
    style={{ position: "absolute", left: 12, top: 8 }}
    strokeWidth="2"
    stroke="currentColor">
    <path strokeLinecap="round" strokeLinejoin="round" d="M3 16.5v2.25A2.25 2.25 0 005.25 21h13.5A2.25 2.25 0 0021 18.75V16.5M16.5 12L12 16.5m0 0L7.5 12m4.5 4.5V3" />
</svg>

const Video = styled.video<{
    borderCss: string,
}>`
    width: 100%;
    height: 240px;

    ${({ borderCss }) => borderCss};

`


export default function MediaContent({
    type,
    url,
    size,
    last,
    single,
    messageType
}: Props) {

    return (
        <>
            {(type === 'image' || type === 'gif') &&
                <ImageContainer>
                    <Image
                        borderCss={(() => getBorderCss({
                            type: messageType,
                            last,
                            single
                        }))()}
                        src={url}
                        alt={url} />
                </ImageContainer>
            }



            {(type === 'file' || type === 'video') &&
                <div style={{ position: "relative", width: "100%" }}>
                    {type === 'video' &&
                        <Video
                            controls
                            borderCss={(() => getBorderCss({
                                type: messageType,
                                last,
                                single
                            }))()}
                        >
                            <source src={url} type="video/mp4" />
                            <source src={url} type="video/ogg" />
                            Your browser does not support the video tag.
                        </Video>
                    }
                    <div style={{ width: "100%", display: 'flex' }}>
                        <FileContainer
                            target='_blank'
                            href={url}>{DownloadIcon}&nbsp;&nbsp;&nbsp;&nbsp;<span style={{ textDecoration: "underline" }}>{url}{size && <SizeText>({size})</SizeText>}</span></FileContainer>
                    </div>
                </div>

            }
        </>
    )
}

import React from 'react'
import styled from 'styled-components'

type Props = {
    children?: string
    color?: string
    linkColor?: string
}

export const Content = styled.div<{
    color?: string
    linkColor?: string
}>`
text-align:left;
vertical-align:text-top;
font-size:14px;
align-self:flex-start;
line-height:auto;
color:${({ color }) => color || '#000000'};
font-family: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
padding-left:16px;
padding-right:16px;
padding-top:8px;
padding-bottom:8px;
position: relative;
box-sizing: border-box;
word-wrap: break-word;
width: 100%;

user-select: none;

a {
    color: ${({ linkColor }) => linkColor || 'blue'};
}

`


export default function TextContent({
    linkColor,
    color,
    children = ""
}: Props) {

    // Regular expression to match URLs
    const urlRegex = /(https?:\/\/[^\s]+)/g;

    return (<Content
        linkColor={linkColor}
        color={color}
        dangerouslySetInnerHTML={{ __html: children.replace(urlRegex, '<a href="$&" target="_blank">$&</a>') }} />
    )
}

function styleInject(css, ref) {
  if ( ref === void 0 ) ref = {};
  var insertAt = ref.insertAt;

  if (!css || typeof document === 'undefined') { return; }

  var head = document.head || document.getElementsByTagName('head')[0];
  var style = document.createElement('style');
  style.type = 'text/css';

  if (insertAt === 'top') {
    if (head.firstChild) {
      head.insertBefore(style, head.firstChild);
    } else {
      head.appendChild(style);
    }
  } else {
    head.appendChild(style);
  }

  if (style.styleSheet) {
    style.styleSheet.cssText = css;
  } else {
    style.appendChild(document.createTextNode(css));
  }
}

export default styleInject;

import React from 'react'
import styled from 'styled-components'
import "./index.css"

type Props = {
    color?: string
}


const Container = styled.div`
    display: flex;
    justify-content: center;
    align-items: center
    ;
`

const InnerContainer = styled.div<{
    color?: string
}>`
box-sizing: border-box;
display: block;
position: absolute;
width: 8px;
height: 8px;

position: absolute;
left: 0;
right: 0;
margin-left: auto;
margin-right: auto;
margin-top: auto;
margin-bottom: auto;
top: 0;
bottom: 0;

/* margin: 6px; */
border: 2px solid ${({ color }) => color || '#fff'};
border-radius: 50%;
animation: lds-ring 1.2s cubic-bezier(0.5, 0, 0.5, 1) infinite;
border-color:  ${({ color }) => color || '#fff'} transparent transparent transparent;
`

export default function Loading({ color }: Props) {

    return (
        <Container className='fade-animation-slow'>
            <div className="message-lds-ring">
                <InnerContainer color={color} />
                <div />
                <div />
                <div />
            </div>

        </Container >
    )
}

function calculateDifferences(date: Date) {
    try {
        const currentDate = new Date()
        const timeDifference = (new Date(currentDate.toUTCString())).getTime() - (new Date(date ? date.toUTCString() : "")).getTime();
        const minutesAgo = Math.floor(timeDifference / (1000 * 60));
        const hoursAgo = Math.floor(minutesAgo / 60);
        const daysAgo = Math.floor(hoursAgo / 24);

        return {
            minutesAgo,
            hoursAgo,
            daysAgo
        }
    } catch (e) {
        return {
            minutesAgo: 0,
            hoursAgo: 0,
            daysAgo: 0
        }
    }
}


export function calculateLastSeen(date: Date): string {
    const diff = calculateDifferences(date)

    if (diff.minutesAgo < 1) {
        return 'Active now'
    } else if (diff.minutesAgo === 1) {
        return 'Seen 1 minute ago'
    } else if (diff.minutesAgo < 60) {
        return `Seen ${diff.minutesAgo} minutes ago`
    } else if (diff.hoursAgo === 1) {
        return 'Seen 1 hour ago'
    } else if (diff.hoursAgo < 24) {
        return `Seen ${diff.hoursAgo} hours ago`
    } else if (diff.daysAgo === 1) {
        return 'Seen 1 day ago'
    } else {
        return `Seen ${diff.daysAgo} days ago`
    }
}

/**
 * 
 * @param date 
 * @returns 
 */
export function calculateTimeAgo(date: Date): string {

    const diff = calculateDifferences(date)

    if (diff.minutesAgo < 1) {
        return 'just now';
    }
    else if (diff.minutesAgo < 60) {
        return `${diff.minutesAgo}m`
    } else if (diff.hoursAgo < 24) {
        return `${diff.hoursAgo}h`
    } else {
        return `${diff.daysAgo}d`
    }
}

import React, { useEffect, useState } from 'react'
import Loading from './loading'
import styled from 'styled-components'
import { calculateTimeAgo } from '../../../utils/date-utils'

type Props = {
    loading?: boolean
    date?: Date
    seen?: boolean
    showSeen?: boolean
    color?: string
    loaderColor?: string
    checkmarkColor?: string
}


const LoadingContainer = styled.div<{
    color?: string
}>`
    position: relative;
    height: 100%;
    display: flex;
    align-items: center;
    margin-right:4px;
    margin-left:2px;


    ${({ color }) => color ? `color: ${color};` : ''}
`

export const Content = styled.div<{
    color?: string
}>`
text-align:right;
vertical-align:text-top;
font-size:12px;

margin-right:2px;
align-self:flex-start;
line-height:auto;
color: ${({ color }) => color || 'rgb(75 85 99)'};
font-family: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
`

const Check = styled.div<{
    color?: string
}>`
position:relative;
width:16px;
height:16px;
padding-bottom:4px;
padding-right:4px;
color: ${({ color }) => color || 'rgb(75 85 99)'};
`

const PlaceholderCheck = styled(Check)`
width:8px;
`

const Container = styled.div`
display:flex;
width: 100%; 
position: relative;
justify-content: end;
align-items: center;
margin-top: -8px;
user-select: none;

`

export default function Timestamp({
    loading,
    date,
    showSeen,
    seen,
    color,
    loaderColor,
    checkmarkColor
}: Props) {

    const [dateSent, setDateSent] = useState<string | undefined>()

    useEffect(() => {
        function updateDateSent() {
            if (date) {
                setDateSent(calculateTimeAgo(date))
            }
        }

        updateDateSent()

        const intervalId = setInterval(() => updateDateSent(), 60_000)

        return () => clearInterval(intervalId);

    }, [])

    return (
        <Container>

            <Content color={color}>{dateSent}</Content>

            {loading ?
                <LoadingContainer  > <Loading color={loaderColor} /> </LoadingContainer>
                :
                (showSeen ?


                    <Check
                        color={checkmarkColor}>
                        {seen ?


                            <svg xmlns="http://www.w3.org/2000/svg"
                                width="16px"
                                height="16px"
                                fill="none"
                                stroke="currentColor"
                                viewBox="0 0 24 24"
                                strokeWidth="1.5"><path
                                    fillRule="evenodd"
                                    d="m6 16.293 9.646-9.647.708.708-10 10a.5.5 0 0 1-.708 0l-4-4 .708-.708L6 16.293zm6 0 9.646-9.647.707.708-9.999 10a.5.5 0 0 1-.707 0l-1.5-1.5.707-.708L12 16.293z"
                                    clipRule="evenodd"></path></svg>
                            :

                            <svg xmlns="http://www.w3.org/2000/svg"
                                width="16px"
                                height="16px"
                                viewBox="0 0 24 24"><path fill="none" d="M0 0h24v24H0V0z"></path>
                                <path
                                    fill='currentColor'
                                    d="M9 16.17L5.53 12.7c-.39-.39-1.02-.39-1.41 0-.39.39-.39 1.02 0 1.41l4.18 4.18c.39.39 1.02.39 1.41 0L20.29 7.71c.39-.39.39-1.02 0-1.41-.39-.39-1.02-.39-1.41 0L9 16.17z"></path></svg>


                        }
                    </Check>
                    :
                    <PlaceholderCheck />
                )
            }
        </Container>
    )
}

import { createContext } from "react";

export default createContext<{
    colorSet?: {}
    themeColor: string
}>({
    colorSet: {},
    themeColor: '#6ea9d7'
})

import { useContext } from "react";
import MinChatUIContext from "../contexts/MinChatUIContext";



export default function useColorSet(label: string): string | undefined {

    const { colorSet } = useContext(MinChatUIContext)

    return colorSet ? (colorSet as any)[label] : undefined
}

import React, { useContext } from 'react'
import styled from 'styled-components'
import MediaContent from '../media-content'
import { getBorderCss } from '../borderController'
import TextContent from '../text-content'
import Timestamp from '../timestamp'
import { Props } from '..'
import useColorSet from '../../../hooks/useColorSet'
import MinChatUIContext from '../../../contexts/MinChatUIContext'


export const Wrapper = styled.div<{ firstMessage?: boolean, lastMessage?: boolean }>`
    display:flex;
    justify-content: end;
    margin-right: 10px;
    margin-top: ${({ firstMessage }) => firstMessage ? "16px" : "2px"};
    position: relative;
    box-sizing: border-box;
    margin-bottom: ${({ lastMessage }) => lastMessage ? "16px" : "2px"};
    z-index: 1;
`



export const Container = styled.div`
max-width:272px;
min-width:80px;
margin-left: 10px;
justify-content:flex-end;
align-items:flex-end;
gap:10px;
position:relative;
box-sizing: border-box;
`
export const Background = styled.div<{
    bgColor: string,
    borderCss: string,
}>`
    position: absolute;
    width: 100%;
    height: 100%;
    background-color:${({ bgColor }) => bgColor};

    ${({ borderCss }) => borderCss};
`






export default function MyMessage({
    text,
    media,
    loading,
    last,
    single,
    clusterFirstMessage,
    clusterLastMessage,
    created_at,
    seen
}: Omit<Props, "showHeader" | "showAvatar" | "type">) {

    const { themeColor } = useContext(MinChatUIContext)

    const textColor = useColorSet("--outgoing-message-text-color")
    const backgroundColor = useColorSet("--outgoing-message-background-color")
    const timestampColor = useColorSet("--outgoing-message-timestamp-color")
    const checkmarkColor = useColorSet("--outgoing-message-checkmark-color")
    const loaderColor = useColorSet("--outgoing-message-loader-color")
    const linkColor = useColorSet("--outgoing-message-link-color")






    return (
        <Wrapper
            data-testid="outgoing-message"
            lastMessage={clusterLastMessage}
            firstMessage={clusterFirstMessage}
            className='fade-animation'
        >
            <div>


                <Container>
                    <Background
                        borderCss={(() => getBorderCss({
                            type: "outgoing",
                            last,
                            single
                        }))()}
                        bgColor={backgroundColor || themeColor} />

                    {media ? <MediaContent
                        last={last}
                        single={single}
                        messageType='outgoing'
                        {...media} />
                        :
                        <TextContent
                            linkColor={linkColor}
                            color={textColor}
                        >{text}</TextContent>}

                    <Timestamp
                        showSeen
                        color={timestampColor}
                        loaderColor={loaderColor}
                        checkmarkColor={checkmarkColor}
                        date={created_at}
                        seen={seen}
                        loading={loading} />

                </Container>

            </div>
        </Wrapper>
    )
}

import React, { useContext, useEffect } from 'react'
import styled from 'styled-components'
import { Container as MyMessageContainer, Wrapper as MyMessageWrapper, Background } from '../outgoing-message'
import placeholderProfilePNG from './profile.webp'
import MediaContent from '../media-content'
import { getBorderCss } from '../borderController'
import TextContent from '../text-content'
import { Props } from '..'
import Timestamp from '../timestamp'
import useColorSet from '../../../hooks/useColorSet'
import MinChatUIContext from '../../../contexts/MinChatUIContext'


const MessageContainer = styled(MyMessageContainer)`
    margin-left: 0px;
    box-sizing: border-box;
    margin-bottom: 0px;
`


const Wrapper = styled(MyMessageWrapper)`
justify-content: start;
align-items: flex-end;
`

const DPContainer = styled.div`
    width: 32px;
    height: 32px;
    margin-left: 10px;
    box-sizing: border-box;
    user-select: none;

`
const DisplayPicture = styled.img`
    width: 32px;
    height: 32px;
    border-radius: 9999px;
    box-sizing: border-box;
    border-width: 2px;
    border-color: rgb(255 255 255);
    object-fit: cover;
    position: relative;
    z-index: 1;
`



const Name = styled.div<{
    color?: string
}>`
text-align:left;
vertical-align:text-top;
font-size:14px;
align-self:flex-start;
line-height:auto;
color:${({ color }) => color || "#4b5563"};
font-family: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
font-weight: 500;
user-select: none;

`

const TextWrapper = styled.div`
margin-left:8px;
box-sizing: border-box;
`


const IncomingMessageBackground = styled(Background) <{
    backgroundColor?: string
}>`
    ${({ backgroundColor }) => !backgroundColor ? "opacity: 0.5;" : ""}
`

const HeaderContainer = styled.div`
 display: flex; 
 align-items: "center";
 margin-top: 16px;
 margin-bottom: 6px;
 `



export default function IncomingMessage({
    text,
    media,
    user,
    showAvatar,
    showHeader,
    last,
    single,
    created_at,
}: Omit<Props, "type" | "clusterFirstMessage" | "clusterLastMessage" | "seen">) {

    const { themeColor } = useContext(MinChatUIContext)

    const [avatar, setAvatar] = React.useState<string>(placeholderProfilePNG)

    useEffect(() => {
        if (user?.avatar && user.avatar.trim().length > 0) {
            setAvatar(user.avatar)
        }
    }, [user])


    const textColor = useColorSet("--incoming-message-text-color")
    const nameTextColor = useColorSet("--incoming-message-name-text-color")
    const linkColor = useColorSet("--incoming-message-link-color")

    const backgroundColor = useColorSet("--incoming-message-background-color")
    const timestampColor = useColorSet("--incoming-message-timestamp-color")

    return (
        <Wrapper
            data-testid="incoming-message"
            className='fade-animation'
        >
            <DPContainer>
                {showAvatar &&

                    <DisplayPicture
                        onError={() => {
                            setAvatar(placeholderProfilePNG)
                        }}
                        src={avatar}
                    />}
            </DPContainer>

            <TextWrapper>
                {showHeader &&
                    <HeaderContainer>
                        <Name color={nameTextColor}>{user?.name}</Name>
                    </HeaderContainer>
                }

                <div style={{ display: "flex" }}>
                    <MessageContainer>
                        <IncomingMessageBackground
                            borderCss={(() => getBorderCss({
                                type: "incoming",
                                last,
                                single
                            }))()}
                            backgroundColor={backgroundColor}
                            bgColor={backgroundColor || themeColor} />

                        {media ? <MediaContent
                            last={last}
                            single={single}
                            messageType='incoming'
                            {...media} />
                            :
                            <TextContent
                                linkColor={linkColor}
                                color={textColor}>{text}</TextContent>}

                        <Timestamp
                            color={timestampColor}
                            date={created_at}
                        />
                    </MessageContainer>
                </div>

            </TextWrapper>
        </Wrapper>
    )
}

import React from 'react'
import User from '../../types/UserType';
import OutgoingMessage from './outgoing-message'
import IncomingMessage from './incoming-message'
import { MediaType } from '../../types/MessageType';


export type Props = {
    created_at?: Date
    seen?: boolean
    text?: string,
    media?: MediaType,
    loading?: boolean
    type?: "incoming" | "outgoing"
    user?: User
    showAvatar?: boolean
    showHeader?: boolean
    // determines whether its the last message in the group of outgoing or incoming
    last?: boolean
    //determines whether its the only message in the group of outgoing or incoming
    single?: boolean
    clusterFirstMessage?: boolean
    clusterLastMessage?: boolean

};


export default function Message({
    text,
    media,
    created_at,
    seen,
    loading,
    type = "outgoing",
    user,
    showAvatar,
    showHeader,
    last,
    single,
    clusterFirstMessage,
    clusterLastMessage
}: Props) {

    return (
        type === "outgoing" ?
            <OutgoingMessage
                loading={loading}
                text={text}
                created_at={created_at}
                seen={seen}
                media={media}
                last={last}
                single={single}
                clusterFirstMessage={clusterFirstMessage}
                clusterLastMessage={clusterLastMessage}
            />

            :

            <IncomingMessage
                showAvatar={showAvatar}
                text={text}
                created_at={created_at}
                media={media}
                user={user}
                showHeader={showHeader}
                last={last}
                single={single}
            />

    )
}

