import React from 'react';

function KeyModal({ isOpen, onSubmit }) {
    const [apikey, setApikey] = React.useState('');
    const [org, setOrg] = React.useState('');

    const handleKeyChange = (event) => {
        setApikey(event.target.value);
    };

    const handleOrgChange = (event) => {
        setOrg(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault(); // Prevent the form from refreshing the page
        onSubmit(apikey, org); // Pass the key and org to the parent component
    };

    if (!isOpen) {
        return null;
    }

    return (
        <div style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, backgroundColor: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', zIndex: '1001', justifyContent: 'center' }}>
            <div style={{ background: 'white', padding: 20, borderRadius: 8 }}>
                <form onSubmit={handleSubmit}>
                    <label>
                        OpenAI API-Key:
                        <input type="password" value={apikey} onChange={handleKeyChange} autoFocus />
                    </label>
                    <label>
                        OpenAI ORG ID:
                        <input type="password" value={org} onChange={handleOrgChange} placeholder='optional'/>
                    </label>
                    <button type="submit">Enter</button>
                </form>
            </div>
        </div>
    );
}

export default KeyModal;
