import React, { useState,  useEffect } from 'react';
import './App.css';
import KeyModal from './KeyModal';

document.addEventListener('DOMContentLoaded', function() {
  fetch('/delete-files', {
      method: 'POST',
      headers: {
          'Content-Type': 'application/json'
      }
  })
  .then(response => response.json())
  .then(data => console.log('Success:', data))
  .catch((error) => {
      console.error('Error:', error);
  });
});


function App() {
  const [messages, setMessages] = useState([]);
  const [backendOutputs, setBackendOutputs] = useState([]);
  const [description, setDescription] = useState(''); 
  const [extractedSentences, setExtractedSentences] = useState([]);

  const [imageUrl, setImageUrl] = useState('');
  const [showImage, setShowImage] = useState(false);

  const [file, setFile] = useState(null);

  const [isModalOpen, setIsModalOpen] = useState(true);

  useEffect(() => {
      // Automatically open the modal when the page loads
      setIsModalOpen(true);
  }, []);

  const handleKeySubmit = async (apikey, org) => {
    setIsModalOpen(false); // Close the modal after submitting
    // Here you can call your backend API
    try {
        const response = await fetch('http://127.0.0.1:5000/process_key', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ apikey, org }),
        });
        const data = await response.json();
        setIsModalOpen(false); // Close the modal after submitting

    } catch (error) {
        console.error('Error submitting password:', error);
    }
};


  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handleUpload = () => {
    if (!file) {
      alert('Please select a file first!');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    fetch('http://127.0.0.1:5000/upload-csv', {
      method: 'POST',
      body: formData,
    })
    .then(response => response.json())
    // .then(data => alert('Success: ' + data.message))
    // .catch(error => alert('Error: ' + error));
  };

  useEffect(() => {
    // Polling mechanism to periodically check for the image
    const interval = setInterval(() => {
      // Update the image URL with a new timestamp to avoid cache issues
      setImageUrl(`/static/dot_graph.png?timestamp=${new Date().getTime()}`);
      setShowImage(true); 
    }, 5000); // Update every 5 seconds

    return () => clearInterval(interval); // Cleanup on component unmount
  }, []);

  const handleSendMessage = (message) => {
    const payload = {
      message,  // the message text from the input
      description  // the current state of the description input
    };
    // POST request to start the count
    fetch('http://127.0.0.1:5000/start_count', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload)
    })

    .then(() => {
      // Establish an EventSource connection to receive the count
      const eventSource = new EventSource('http://127.0.0.1:5000/test_stream');
      eventSource.onmessage = event => {
        const data = event.data.trim();
        if (data.startsWith("########## Feedback: ")) {
          const remainingMessage = data.slice("########## Feedback: ".length).trim(); // Remove "WARNING:" and trim any extra whitespace
          const pattern = /\*\*(.*?)\*\*/g;
          const extractedSentences = remainingMessage.match(pattern).map(match => match.slice(2, -2));
          const replacedMessage = remainingMessage.replace(pattern, (match) => {
            return match.replace(/\*\*/g, "'");
          });
          setMessages(prev => [...prev, { text: replacedMessage, from: 'bot' }]);
          // setExtractedSentences(extractedSentences);
        } else {
          setBackendOutputs(prev => [...prev, data]);
        }
      };
      eventSource.onerror = error => {
        console.error('EventSource error:', error);
        eventSource.close();
      };
    })

    .catch(error => console.error('Error:', error));

    setMessages(prev => [...prev, { text: message, from: 'user' }]);
    // setDescription('');
  };

  const handleMessageInput = event => {
    if (event.key === 'Enter' && event.target.value.trim() !== '') {
      handleSendMessage(event.target.value);
      event.target.value = '';
      setBackendOutputs([]);
    }
  };

  return (
    <div className="App">
      <KeyModal isOpen={isModalOpen} onSubmit={handleKeySubmit} /> 
      <div className="grid-container">
        <div className="chat-section">
          <header className="App-header">
            <ul>
              {messages.map((msg, index) => (
                <li key={index} style={{ textAlign: msg.from === 'user' ? 'right' : 'left' }}>
                  {msg.text}
                </li>
              ))}
            </ul>
            <div className="extracted-section">
              {/* <h2>Extracted Sentences:</h2> */}
              {extractedSentences && extractedSentences.map((sentence, index) => (
                <div key={index} style={{ marginBottom: '10px', border: '1px solid #ccc', padding: '5px' }}>
                  {sentence}
                </div>
              ))}
          </div>
            <div>
              <input type="file" onChange={handleFileChange} accept=".csv" />
              <button onClick={handleUpload}>Upload File</button>
            </div>
            <div>
              <input type="text" value={description} onChange={handleDescriptionChange} placeholder="Type data description..." />
            </div>
            <input type="text" onKeyDown={handleMessageInput} placeholder="Type query..." />
          </header>
        </div>
        <div className="output-section">
          <h2>LEAP Output:</h2>
          <div>
          {showImage && (
            <div>
              <img src={imageUrl} alt="Dynamic from backend" onError={(e) => {
                console.error('Failed to load image:', imageUrl);
                setShowImage(false); // Hide the image if there is an error loading it
              }} />
              <div className="subtitle">Column Mapping Relationship</div>
            </div>
          )}
          </div>
          <pre>
            {backendOutputs.map((output, index) => (
              <div key={index}>{output}</div>
            ))}
          </pre>
        </div>
      </div>
    </div>
  );
}

export default App;
